    _id = id;
    _senderId = senderId;
    _receiverId = receiverId;
    _title = title;
    _subtitle = subtitle;
    _campaignId = campaignId;
    _drawId = drawId;
    _status = status;
    _createdAt = createdAt;
    _updatedAt = updatedAt;
    _campaign = campaign;
  }

  ApiNotification.fromJson(dynamic json) {
    _id = json['id'];
    _senderId = json['sender_id'];
    _receiverId = json['receiver_id'];
    _title = json['title'];
    _subtitle = json['subtitle'];
    _campaignId = json['campaign_id'];
    _drawId = json['draw_id'];
    _status = json['status'];
    _createdAt = json['created_at'];
    _updatedAt = json['updated_at'];
    _campaign = json['campaign'] != null ? ApiCampaign.fromJson(json['campaign']) : null;
  }
  int? _id;
  int? _senderId;
  int? _receiverId;
  String? _title;
  String? _subtitle;
  int? _campaignId;
  int? _drawId;
  String? _status;
  String? _createdAt;
  dynamic _updatedAt;
  ApiCampaign? _campaign;
  ApiNotification copyWith({  int? id,
    int? senderId,
    int? receiverId,
    String? title,
    String? subtitle,
    int? campaignId,
    int? drawId,
    String? status,
    String? createdAt,
    dynamic updatedAt,
    ApiCampaign? campaign,
  }) => ApiNotification(  id: id ?? _id,
    senderId: senderId ?? _senderId,
    receiverId: receiverId ?? _receiverId,
    title: title ?? _title,
    subtitle: subtitle ?? _subtitle,
    campaignId: campaignId ?? _campaignId,
    drawId: drawId ?? _drawId,
    status: status ?? _status,
    createdAt: createdAt ?? _createdAt,
    updatedAt: updatedAt ?? _updatedAt,
    campaign: campaign ?? _campaign,
  );
  int? get id => _id;
  int? get senderId => _senderId;
  int? get receiverId => _receiverId;
  String? get title => _title;
  String? get subtitle => _subtitle;
  int? get campaignId => _campaignId;
  int? get drawId => _drawId;
  String? get status => _status;
  String? get createdAt => _createdAt;
  dynamic get updatedAt => _updatedAt;
  ApiCampaign? get campaign => _campaign;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['id'] = _id;
    map['sender_id'] = _senderId;
    map['receiver_id'] = _receiverId;
    map['title'] = _title;
    map['subtitle'] = _subtitle;
    map['campaign_id'] = _campaignId;
    map['draw_id'] = _drawId;
    map['status'] = _status;
    map['created_at'] = _createdAt;
    map['updated_at'] = _updatedAt;
    if (_campaign != null) {
      map['campaign'] = _campaign?.toJson();
    }
    return map;
  }

